import React, { useState } from 'react';
import { GreeterClient } from './proto/greet_grpc_web_pb';
import { HelloRequest } from './proto/greet_pb';
import { Container, TextField, Button, Typography } from '@mui/material';

const client = new GreeterClient('https://localhost:5001', null, null);

function App() {
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');

  const handleClick = () => {
    const request = new HelloRequest();
    request.setName(name);

    client.sayHello(request, {}, (err, response) => {
      if (err) {
        console.error(err);
        return;
      }
      setMessage(response.getMessage());
    });
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      handleClick();
    }
  };

  return (
    <div
      style={{
        backgroundImage: 'url(https://i0.wp.com/i.postimg.cc/s2qHNPzW/grpc.png?resize=1280%2C680&ssl=1)', // Replace with your image URL
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <Container
        style={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          borderRadius: '8px',
          padding: '40px',
          maxWidth: '600px',
          textAlign: 'center',
        }}
      >
        <Typography variant="h4" gutterBottom>
          gRPC React Client
        </Typography>
        <TextField
          label="Enter your message"
          variant="outlined"
          fullWidth
          value={name}
          onChange={(e) => setName(e.target.value)}
          onKeyDown={handleKeyDown}
          margin="normal"
        />
        <Button variant="contained" color="primary" onClick={handleClick} style={{ marginTop: 16 }}>
          Make the gRPC Call
        </Button>
        <Typography variant="h6" style={{ marginTop: 20 }}>
          {message}
        </Typography>
      </Container>
    </div>
  );
}

export default App;
